import Axios from "axios";
import { CART_EMPTY, ORDER_CREATE_FAIL, ORDER_CREATE_RESET, ORDER_CREATE_SUCCESS, ORDER_DETAILS_FAIL, ORDER_DETAILS_REQUEST, ORDER_DETAILS_SUCCESS } from "../constants/orderConstants"

export const createOrder = (order) => async(dispatch, getState) =>{
    dispatch({type: ORDER_CREATE_RESET, payload: order});
    try {
        // getState returns the whole store Redux for us! WOW
        const {userSignin: {userInfo}} = getState();
        const {data} = await Axios.post('/api/orders', order, {
            headers: {
                Authorization: `Bearer ${userInfo.token}`, 
            },
        });
        dispatch({type: ORDER_CREATE_SUCCESS, payload: data.order});
        dispatch({type: CART_EMPTY});
        localStorage.removeItem('cartItems');

    } catch(error) {
        dispatch({
            type: ORDER_CREATE_FAIL,
            payload: 
            error.response && error.response.data.message
            ? error.response.data.message
            : error.message,
        });
    };
}

export const detailsOrder = (orderId) => async (dispatch, getState ) => {
    dispatch({type: ORDER_DETAILS_REQUEST, payload: orderId});

    const {userSignin: {userInfo},
      } = getState();

    try {
        const {data} = await Axios.get(`/api/orders/${orderId}`, {
            headers: {Authorization: `Bearer ${userInfo.token}`},
        });
        dispatch({type: ORDER_DETAILS_SUCCESS, payload: data});

    } catch(error) {
        const message = 
        error.response && error.response.data.message
            ? error.response.data.message
            : error.message;
        dispatch({type: ORDER_DETAILS_FAIL, payload: message});

    }
};
