import React,{useEffect} from 'react'
import { useSelector, useDispatch } from 'react-redux';
import {createProduct, deleteProduct, listProducts} from '../actions/productActions';
import {Link} from 'react-router-dom';
import { useParams } from 'react-router-dom';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import { 
    PRODUCT_CREATE_RESET, 
    PRODUCT_DELETE_RESET} from '../constants/productConstants';
// import product from '../components/Product'


export default function ProductListScreen(props) {
    const {pageNumber = 1 } = useParams();

    const sellerMode = props.match.path.indexOf('/seller') >= 0;

    const productList = useSelector(state => state.productList);
    // const {loading, error, products} = productList;
    const {loading, error, products, page, pages} = productList;

    const productCreate = useSelector((state) => state.productCreate);
    const {
        loading: loadingCreate, 
        error: errorCreate, 
        success: successCreate, 
        product: createdProduct, 
        } = productCreate;

        const productDelete = useSelector((state) => state.productDelete);
        const {
            loading: loadingDelete, 
            error: errorDelete, 
            success: successDelete,
        } = productDelete;

        const userSignin = useSelector((state) => state.userSignin);
        const {userInfo} = userSignin;

        const dispatch = useDispatch();

    useEffect(() => {

        if(successCreate){ // itmeans that i have successfully created product
            dispatch({type: PRODUCT_CREATE_RESET});
            props.history.push(`/product/${createdProduct._id}/edit`);
        }
        if(successDelete){
            dispatch({type: PRODUCT_DELETE_RESET})
        }
        // dispatch(listProducts());
        dispatch(listProducts({ seller: sellerMode ? userInfo._id : '', pageNumber }));

    },[createdProduct, dispatch, props.history, sellerMode, successCreate, successDelete, userInfo._id, pageNumber]);



    const deleteHandler = (product) => {
        if(window.confirm("Are you sure you wonna delete this mate?")){
            /// todo: dispatch delete action
        dispatch(deleteProduct(product._id));

        }

    };

    const createHandler = ()=>{
        dispatch(createProduct());
    }


    return (
        <div>
            <div className="row">
            <h1>Products</h1>
            <button type="button" className="promary" onClick={createHandler}>
                Create Product
            </button>

            </div>

            {loadingDelete && <LoadingBox></LoadingBox>}
            {errorDelete && <MessageBox variant="danger">{errorDelete}</MessageBox>}

            {loadingCreate && <LoadingBox></LoadingBox>}
            {errorCreate && <MessageBox variant="danger">{errorCreate}</MessageBox>}

            {loading ? (
            <LoadingBox></LoadingBox>
            ) : error ? (
            <MessageBox variant="danger">{error}</MessageBox>
            ) : (
                <>
                <table className="table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>NAME</th>
                            <th>PRICE</th>
                            <th>CATEGORY</th>
                            <th>BRAND</th>
                            <th>ACTIONS</th>
                        </tr>
                    </thead>

                    <tbody>
                        {products.map((product)=>(
                            <tr key={product._id}>
                                <td>{product._id}</td>
                                <td>{product.name}</td>
                                <td>{product.price}</td>
                                <td>{product.category}</td>
                                <td>{product.brand}</td>
                                <td>
                                    <button 
                                    type="button"
                                    className="small"
                                    onClick={() => 
                                    props.history.push(`/product/${product._id}/edit`)
                                }   
                                >Edite
                                </button>
                                <button 
                                type="button" 
                                className="small"
                                onClick={() =>deleteHandler(product)}
                                >
                                Delete
                                </button>
                                </td>

                            </tr>
                        ))}
                    </tbody>
                </table>

                <div className="row center pagination">
                {[...Array(pages).keys()].map((x) =>(
                        <Link className={x +1 === page? 'active' : ''} key={x + 1} to={`/productlist/pageNumber/${page}`}>
                            {x + 1}
                            </Link>))}
                </div>
                </>


            )}  
        </div>
    )
}
