import { FAV_DETAILS_FAIL, FAV_DETAILS_REQUEST, FAV_DETAILS_SUCCESS } from "../constants/favoriteCartConstants";



export const favoriteListReducer = (
    state = {loading: true, favorites: []},
     action
     ) => {
    switch (action.type){
        case FAV_DETAILS_REQUEST:
            return {loading: true};
        case FAV_DETAILS_SUCCESS:
            // return {loading: false, products: action.payload};
            return {loading: false, favorites: action.payload.favorites,
            pages: action.payload.pages,
            page: action.payload.page,
            };
        case FAV_DETAILS_FAIL:
                return {loading: false, error: action.payload};
        default:
                return state;
    }
};
