import React, {useState} from 'react';
import {useDispatch, useSelector} from 'react-redux';
import { savePaymentMethod } from '../actions/cartActions';
import CheckoutSteps from '../components/CheckoutSteps';

export default function PaymentMethodScreen(props) {
    // this is if the user didnt add the shipping address
    const cart = useSelector((state) => state.cart);
    const {shippingAddress} = cart;
    if(!shippingAddress.address){
        props.history.push('/shipping');
    }


    // const [paymentMethod, setPaymentMethod] = useState('PayPal');
    const [paymentMethod, setPaymentMethod] = useState('Pay On Cash');

    const dispatch = useDispatch();

    const submitHandler = (e) =>{
        e.preventDefault();
        dispatch(savePaymentMethod(paymentMethod));
        props.history.push('/placeorder');
    };

    return (
        <div>
            <CheckoutSteps step1 step2 step3 ></CheckoutSteps>
            <form className="form" onSubmit={submitHandler}>
                <div>
                    {/* <h1>Choose Payment Method</h1> */}
                    <h1>Payment Method</h1>
                </div>

                {/* <div>
                    <div>
                        <input 
                          type="radio" 
                          id="paypal" 
                          value="PayPal" 
                          name="paymentMethod"
                          required 
                          checked onChange={(e) =>setPaymentMethod(e.target.value)}
                        >
                        </input>

                        <label htmlFor="paypal">PayPal</label>
                    </div>
                </div> */}

                <div>
                    <div>
                        <input 
                          type="radio" 
                          id="payOnCash" 
                          value="payOnCash" 
                          name="paymentMethod"
                          required 
                          checked onChange={(e) =>setPaymentMethod(e.target.value)}
                        >
                        </input>

                        <label htmlFor="payOnCash">Pay On Cash</label>
                    </div>
                </div>


                {/* <div>
                    <div>
                        <input type="radio" id="stripe" value="Stripe" name="paymentMethod"
                        required onChange={(e) =>setPaymentMethod(e.target.value)}
                        >
                        </input>

                        <label htmlFor="stripe">Stripe</label>
                    </div>
                </div> */}

                <div>
                    <button className="primary" type="submit">Continue</button>
                </div>


            </form>
        </div>
    )
}
