import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { listCategories } from "../actions/categoryActions";

// import {createProduct, deleteProduct, listProducts} from '../actions/productActions';
// import {Link} from 'react-router-dom';
// import { useParams } from 'react-router-dom';
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import data from "../data";
// import {
//   PRODUCT_CREATE_RESET,
//   PRODUCT_DELETE_RESET,
// } from "../constants/productConstants";

export default function CategoryListScreen(props) {
  const categorytList = useSelector((state) => state.categorytList);
  const { loading, error, categories } = categorytList;
  console.log("categories kaka");
  //   console.log(categories);

  const dispatch = useDispatch();

  useEffect(() => {
    // if (successCreate) {
    //   dispatch({ type: PRODUCT_CREATE_RESET });
    //   props.history.push(`/product/${createdProduct._id}/edit`);
    // }
    // if (successDelete) {
    //   dispatch({ type: PRODUCT_DELETE_RESET });
    // }
    dispatch(
      // listCategories({ seller: sellerMode ? userInfo._id : "", pageNumber })
      listCategories()
    );
  }, [
    // createdProduct,
    dispatch,
    // props.history,
    // sellerMode,
    // successCreate,
    // successDelete,
    // userInfo._id,
    // pageNumber,
  ]);

  const deleteHandler = (product) => {
    // if(window.confirm("Are you sure you wonna delete this mate?")){
    // todo: dispatch delete action
    // dispatch(deleteProduct(product._id));
  };

  return (
    <div>
      {/* <div className="row"> */}
      <h1>Categories</h1>
      {/* <button type="button" className="promary" onClick={createHandler}>
        Create Product
      </button> */}
      {/* </div> */}

      {/* {loadingDelete && <LoadingBox></LoadingBox>}
      {errorDelete && <MessageBox variant="danger">{errorDelete}</MessageBox>}

      {loadingCreate && <LoadingBox></LoadingBox>}
      {errorCreate && <MessageBox variant="danger">{errorCreate}</MessageBox>} */}

      {loading ? (
        <LoadingBox></LoadingBox>
      ) : error ? (
        <MessageBox variant="danger">{error}</MessageBox>
      ) : (
        <>
          <table className="table">
            <thead>
              <tr>
                <th>ID</th>
                <th>NAME</th>
                <th>PRICE</th>
                <th>SIZE</th>
                <th>CATEGORY</th>
                <th>BRAND</th>
                <th>ACTIONS</th>
              </tr>
            </thead>

            <tbody>
              {data.categories.map((category) => (
                <tr key={category._id}>
                  <td>{category._id}</td>
                  <td>{category.name}</td>
                  <td>{category.price}</td>
                  <td>{category.size}</td>
                  <td>{category.category}</td>
                  <td>{category.brand}</td>
                  <td>
                    <button
                      type="button"
                      className="small"
                      onClick={() =>
                        props.history.push(`/category/${category._id}/edit`)
                      }
                    >
                      Edite
                    </button>
                    <button
                      type="button"
                      className="small"
                      onClick={() => deleteHandler(category)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* <div className="row center pagination">
            {[...Array(pages).keys()].map((x) => (
              <Link
                className={x + 1 === page ? "active" : ""}
                key={x + 1}
                to={`/productlist/pageNumber/${x + 1}`}
              >
                {x + 1}
              </Link>
            ))}
          </div> */}
        </>
      )}
    </div>
  );
}
