import React from "react";
import styled, { keyframes } from "styled-components";

export default function TextAnimation() {
  const reactArray = "Glossy".split("");

  return (
    <Wrapper>
      {reactArray.map((item, index) => (
        <span key={index}>{item}</span>
      ))}
    </Wrapper>
  );
}

