import React, {useEffect} from 'react';
import 'react-responsive-carousel/lib/styles/carausel.min.css';
import {Carousel} from 'react-responsive-carousel';
import Product from "../components/Product";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox"
import { useDispatch, useSelector } from 'react-redux';
import { listProducts } from '../actions/productActions';
import { listTopSellers } from '../actions/userActions';

// import data from "../data"

export default function HomeScreen() {
const dispatch = useDispatch();
const productList = useSelector((state) => state.productList);
const {loading, error, products} = productList;


const userTopSellersListReducer = useSelector((state) => state.userTopSellersListReducer);
const {loading: loadingSellers, error:errorSellers, users: sellers} = userTopSellersListReducer;


  useEffect(() => {
    // dispatch(listProducts());
    dispatch(listProducts({}));
    dispatch(listTopSellers())
  }, [dispatch]);

    return (
        <div>
          {loading? (<LoadingBox></LoadingBox>
          ) : error? (
            <MessageBox variant="danger">{error}</MessageBox>
          ) : (
          <div className="row center">
          {products.map((product)=>(
            <Product key={product._id} product={product}></Product>
            ))
          }
           </div>
          )
          }

           </div>
    )
}
