import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { BrowserRouter, Link, Route, Switch } from "react-router-dom";
import { signout } from "./actions/userActions";
import CartScreen from "./screens/CartScreen";
import HomeScreen from "./screens/HomeScreen";
import OrderHistoryScreen from "./screens/OrderHistoryScreen";
import OrderScreen from "./screens/OrderScreen";
import PaymentMethodScreen from "./screens/PaymentMethodScreen";
import PlaceOrderScreen from "./screens/PlaceOrderScreen";
import ProductScreen from "./screens/ProductScreen";
import ProfileScreen from "./screens/ProfileScreen";
import RegisterScreen from "./screens/RegisterScreen";
import ShippingAddressScreen from "./screens/ShippingAddressScreen";
import SigninScreen from "./screens/SigninScreen";
import PrivateRoute from "./components/PrivateRoute";
import ProductListScreen from "./screens/ProductListScreen";
import AdminRoute from "./components/AdminRoute";

import ProductEditScreen from "./screens/ProductEditScreen";
import OrderListScreen from "./screens/OrderListScreen";
import UserListScreen from "./screens/UserListScreen";
import UserEditScreen from "./screens/UserEditScreen";

import SellerRoute from "./components/SellerRoute";
import SellerScreen from "./screens/SellerScreen";
import SearchBox from "./components/SearchBox";
import SearchScreen from "./screens/SearchScreen";
import { listProductCategories } from "./actions/productActions";
import LoadingBox from "./components/LoadingBox";
import MessageBox from "./components/MessageBox";

import MapScreen from "./screens/MapScreen";
import DashboardScreen from "./screens/DashboardScreen";
import SupportScreen from "./screens/SupportScreen";
import ChatBox from "./components/ChatBox";

import FavoriteScreen from "./screens/FavoriteScreen";
import CategoryListScreen from "./screens/CategoryListScreen";
import CategoryEditScreen from "./screens/CategoryEditScreen";
// import alertBox from './components/AlertBox';
import Navbar from "react-bootstrap/Navbar";
import Container from "react-bootstrap/Container";
import { LinkContainer } from "react-router-bootstrap";
import Badge from "react-bootstrap/Badge";
import Nav from "react-bootstrap/Nav";

// console.log(data)

function App() {
  const cart = useSelector((state) => state.cart);
  const [sidebarIsOpen, setSidebarIsOpen] = useState(false);

  const { cartItems } = cart;

  const userSignin = useSelector((state) => state.userSignin);
  const { userInfo } = userSignin;

  const dispatch = useDispatch();

  const signoutHandler = () => {
    dispatch(signout());
  };

  // ******* favorite into dataBase
  // const favorite = useSelector(state => state.favorite);
  // console.log("test kaka")
  // console.log(favorite)
  // console.log("test kaka")
  // const {favorateProducts} = favorite;

  const favorite = useSelector((state) => state.favorite);
  console.log("test kaka");
  console.log(favorite);
  console.log("test kaka");
  const { favoriteItems } = favorite;

  const productCategoryList = useSelector((state) => state.productCategoryList);
  const {
    loading: loadingCategories,
    error: errorCategories,
    categories,
  } = productCategoryList;

  useEffect(() => {
    dispatch(listProductCategories());
  }, [dispatch]);

  return (
    <BrowserRouter>
      <div className="d-flex flex-column site-container">
        <header>
          <Navbar bg="dark" variant="dark">
            <Container>
              <LinkContainer to="/">
                <Navbar.Brand>amazona</Navbar.Brand>
              </LinkContainer>
              <Nav className="me-auto">
                <Link to="/cart" className="nav-link">
                  Cart
                  {cart.cartItems.length > 0 && (
                    <Badge pill bg="danger">
                      {/* {cart.cartItems.length} */}
                      {cart.cartItems.reduce((a, c) => a + c.qty, 0)}
                    </Badge>
                  )}
                </Link>
              </Nav>

              <button
                type="button"
                className="open-sidebar"
                onClick={() => setSidebarIsOpen(true)}
              >
                <i className="fa fa-bars"></i>
              </button>

              <div>
                <Route
                  render={({ history }) => (
                    <SearchBox history={history}></SearchBox>
                  )}
                ></Route>
              </div>

              <div>
                <Link to="/favorite/:id?">
                  <i className="fa fa-heart" aria-hidden="true"></i>
                  {favoriteItems.length > 0 && (
                    <span className="badge">{favoriteItems.length} </span>
                  )}
                </Link>

                {/* <Link to="/cart">
                  <i className="fa fa-shopping-cart" aria-hidden="true"></i>
                  {cartItems.length > 0 && (
                    <span className="badge">{cartItems.length} </span>
                  )}
                </Link> */}
                {userInfo ? (
                  <div className="dropdown">
                    <Link to="#">
                      {userInfo.name} <i className="fa fa-caret-down"></i>{" "}
                    </Link>
                    <ul className="dropdown-content">
                      <li>
                        <Link to="/profile">User Profile</Link>
                      </li>

                      <li>
                        <Link to="/orderhistory">Order History</Link>
                      </li>

                      <Link to="#signout" onClick={signoutHandler}>
                        Sign Out
                      </Link>
                    </ul>
                  </div>
                ) : (
                  <Link to="/signin">Sign In</Link>
                )}

                {/* seller part */}
                {userInfo && userInfo.isSeller && (
                  <div className="dropdown">
                    <Link to="#admin">
                      Seller <i className="fa fa-caret-down"></i>
                    </Link>
                    <ul className="dropdown-content">
                      <li>
                        <Link to="/productlist/seller">Products</Link>
                      </li>
                      <li>
                        <Link to="/orderlist/seller">Orders</Link>
                      </li>
                    </ul>
                  </div>
                )}

                {userInfo && userInfo.isAdmin && (
                  <div className="dropdown">
                    <Link to="#admin">
                      Admin <i className="fa fa-caret-down"></i>
                    </Link>
                    <ul className="dropdown-content">
                      <li>
                        <Link to="/dashboard">Dashboard</Link>
                      </li>
                      <li>
                        <Link to="/categorylist">Category</Link>
                      </li>

                      <li>
                        <Link to="/productlist">Products</Link>
                      </li>

                      <li>
                        <Link to="/orderlist">Orders</Link>
                      </li>

                      <li>
                        <Link to="/userlist">Users</Link>
                      </li>

                      <li>
                        <Link to="/support">Support</Link>
                      </li>
                    </ul>
                  </div>
                )}
              </div>
            </Container>
          </Navbar>
        </header>

        <aside className={sidebarIsOpen ? "open" : ""}>
          <ul className="categories">
            <li>
              <strong>Categories</strong>
              <button
                onClick={() => setSidebarIsOpen(false)}
                className="close-sidebar"
                type="button"
              >
                <li className="fa fa-close"></li>
              </button>
            </li>

            {loadingCategories ? (
              <LoadingBox></LoadingBox>
            ) : errorCategories ? (
              <MessageBox variant="danger">{errorCategories}</MessageBox>
            ) : (
              categories.map((c) => (
                <li key={c}>
                  <Link
                    to={`/search/category/${c}`}
                    onClick={() => setSidebarIsOpen(false)}
                  >
                    {c}
                  </Link>
                </li>
              ))
            )}
          </ul>
        </aside>

        <main>
          <Container className="mt-3">
            <Switch>
              {/* <Route path="/product/:slug" element={<ProductScreen />} />
              <Route path="/" element={<HomeScreen />} /> */}
              <Route path="/seller/:id" component={SellerScreen}></Route>
              <Route path="/cart/:id?" component={CartScreen}></Route>
              <Route path="/signin" component={SigninScreen}></Route>
              <Route path="/register" component={RegisterScreen}></Route>
              <Route
                path="/product/:id"
                component={ProductScreen}
                exact
              ></Route>
              <Route
                path="/favorite/:id?"
                component={FavoriteScreen}
                exact
              ></Route>
              {/* <Route path="/favoritecart/:id?" component={FavoriteScreen} exact ></Route> */}
              <Route
                path="/product/:id/edit"
                component={ProductEditScreen}
                exact
              ></Route>
              <Route
                path="/category/:id/edit"
                component={CategoryEditScreen}
                exact
              ></Route>
              <Route path="/shipping" component={ShippingAddressScreen}></Route>
              <Route path="/payment" component={PaymentMethodScreen}></Route>
              <Route path="/placeorder" component={PlaceOrderScreen}></Route>
              <Route path="/order/:id" component={OrderScreen}></Route>
              <Route
                path="/orderhistory"
                component={OrderHistoryScreen}
              ></Route>
              <Route
                path="/search/name/:name?"
                component={SearchScreen}
                exact
              ></Route>

              <Route
                path="/search/category/:category"
                component={SearchScreen}
                exact
              ></Route>

              <Route
                path="/search/category/:category/name/:name"
                component={SearchScreen}
                exact
              ></Route>

              <Route
                path="/search/category/:category/name/:name/min/:min/max/:max/rating/:rating/order/:order/pageNumber/:pageNumber"
                component={SearchScreen}
                exact
              ></Route>

              {/* <Route path="/profile" component={ProfileScreen}></Route> */}
              <PrivateRoute
                path="/profile"
                component={ProfileScreen}
              ></PrivateRoute>

              <PrivateRoute path="/map" component={MapScreen}></PrivateRoute>

              <AdminRoute
                path="/productlist"
                component={ProductListScreen}
                exact
              ></AdminRoute>

              {/*  */}
              <AdminRoute
                path="/categorylist"
                component={CategoryListScreen}
                exact
              ></AdminRoute>

              <AdminRoute
                path="/productlist/pageNumber/:pageNumber"
                component={ProductListScreen}
                exact
              ></AdminRoute>

              <AdminRoute
                path="/orderlist"
                component={OrderListScreen}
                exact
              ></AdminRoute>

              <AdminRoute
                path="/userlist"
                component={UserListScreen}
              ></AdminRoute>

              <AdminRoute
                path="/user/:id/edit"
                component={UserEditScreen}
              ></AdminRoute>

              <AdminRoute
                path="/dashboard"
                component={DashboardScreen}
              ></AdminRoute>

              <AdminRoute
                path="/support"
                component={SupportScreen}
              ></AdminRoute>

              <SellerRoute
                path="/productlist/seller"
                component={ProductListScreen}
              ></SellerRoute>

              <SellerRoute
                path="/orderlist/seller"
                component={OrderListScreen}
              ></SellerRoute>

              <Route path="/" component={HomeScreen} exact></Route>
            </Switch>
          </Container>
        </main>

        <footer>
          <div className="text-center">Created by Amini</div>
        </footer>
      </div>
    </BrowserRouter>
  );
}

export default App;
