import React, { useEffect } from "react";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from "react-responsive-carousel";
import Product from "../components/Product";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import { useDispatch, useSelector } from "react-redux";
import { listProducts } from "../actions/productActions";
import { listTopSellers } from "../actions/userActions";
import { Link } from "react-router-dom";

import Topcarusel from "../components/Topcarusel";
import { listCategories } from "../actions/categoryActions";
import Category from "../components/Category";
// import data from "../data"
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { Helmet } from "react-helmet-async";

export default function HomeScreen() {
  const dispatch = useDispatch();
  const productList = useSelector((state) => state.productList);
  const { loading, error, products } = productList;

  const categoryList = useSelector((state) => state.categoryList);
  const {
    loading: loadingCategory,
    error: errorCategory,
    categories,
  } = categoryList;

  const userTopSellersList = useSelector((state) => state.userTopSellersList);
  const {
    loading: loadingSellers,
    error: errorSellers,
    users: sellers,
  } = userTopSellersList;

  useEffect(() => {
    // dispatch(listProducts());
    dispatch(listCategories({}));
    dispatch(listProducts({}));
    dispatch(listTopSellers());
  }, [dispatch]);

  console.log("categories at homeScreen");
  console.log(categories);

  return (
    <div>
      <div className="test1">
        <Topcarusel></Topcarusel>
      </div>
      <Helmet>
        <title>Amazona</title>
      </Helmet>
      <div>
        {loading ? (
          <LoadingBox></LoadingBox>
        ) : error ? (
          <MessageBox variant="danger">{error}</MessageBox>
        ) : (
          <Row>
            <h2>Featured Products</h2>
            {products.length === 0 && (
              <MessageBox>No Product found kaka</MessageBox>
            )}

            {products.map((product) => (
              <Col key={product._id} sm={6} md={4} lg={3} className="mb-3">
                <Product product={product}></Product>
              </Col>
            ))}
          </Row>
        )}
      </div>
      <h2>Categories</h2>

      {loadingCategory ? (
        <LoadingBox></LoadingBox>
      ) : errorCategory ? (
        <MessageBox variant="danger">{error}</MessageBox>
      ) : (
        <Row>
          {categories.length === 0 && (
            <MessageBox>No Categories found kaka</MessageBox>
          )}

          {categories.map((category) => (
            <Col key={category._id} sm={6} md={4} lg={3} className="mb-3">
              <Category category={category}></Category>
            </Col>
          ))}
        </Row>
      )}

      <h2>Top Sellers</h2>
      {loadingSellers ? (
        <LoadingBox></LoadingBox>
      ) : errorSellers ? (
        <MessageBox variant="danger">{errorSellers}</MessageBox>
      ) : (
        <>
          {sellers.length === 0 && (
            <MessageBox>No Seller found kaka</MessageBox>
          )}
          <Carousel showArrows autoPlay showThumbs={false}>
            {sellers.map((seller) => (
              <div key={seller._id}>
                <Link to={`/seller/${seller._id}`}>
                  <img src={seller.seller.logo} alt={seller.seller.name}></img>
                  <p className="legend">{seller.seller.name}</p>
                </Link>
              </div>
            ))}
          </Carousel>
        </>
      )}
    </div>
  );
}
